package com.tnsif.lesson3h;

public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Do while loop
				int i = 1, j = 1;
				do {
					i++;
					j++;
					System.out.println("i=" + i + " and j=" + j);
				    } while (i < 10 || j < 10);
			}

	}

